CREATE TABLE `ims_v_prize_prize_setting` ( `id` int(11) NOT NULL AUTO_INCREMENT, `prize_back` varchar(255) NOT NULL DEFAULT '', `lottery_time` varchar(20) NOT NULL DEFAULT '' COMMENT '开奖时间', `awards_show` text NOT NULL COMMENT '奖品展示', `setting_regulation` text NOT NULL COMMENT '设置规则', `the_winners` text NOT NULL, `last_the_winners` text NOT NULL COMMENT '上期中奖名单', PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
CREATE TABLE `ims_v_prize_shop` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `name` varchar(255) NOT NULL DEFAULT '', `img` varchar(255) NOT NULL DEFAULT '', PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
CREATE TABLE `ims_v_prize_system_setting` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `title` varchar(255) NOT NULL DEFAULT '', `slideshow` text NOT NULL COMMENT '轮播图', `appid` varchar(255) NOT NULL DEFAULT '', `appsecret` varchar(255) NOT NULL DEFAULT '', `phone` varchar(11) NOT NULL DEFAULT '', PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;
CREATE TABLE `ims_v_prize_user` ( `nickname` varchar(255) NOT NULL, `name` varchar(10) NOT NULL DEFAULT '' COMMENT '真实姓名', `avatar` varchar(255) NOT NULL DEFAULT '' COMMENT '头像', `openid` varchar(100) NOT NULL DEFAULT '', `phone` varchar(11) NOT NULL DEFAULT '' COMMENT '电话号码', `address` varchar(255) NOT NULL DEFAULT '' COMMENT '地址', `prize_code` char(7) NOT NULL COMMENT '抽奖码', `lottery_number` tinyint(2) NOT NULL DEFAULT '0', `prize_num` tinyint(4) NOT NULL DEFAULT '0', PRIMARY KEY (`nickname`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE `ims_v_prize_winning_code` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `prize_code` varchar(7) NOT NULL DEFAULT '', PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;
